<script setup>
import { Head, Link, useForm } from '@inertiajs/vue3';
import { ref, onMounted } from 'vue';
import AOS from 'aos';
import 'aos/dist/aos.css';
import { watch } from 'vue';

const props = defineProps({
    blogCount: Number,
    paymentLinks: Object,
    causes: Object,
})

onMounted(() => {
    AOS.init();
});

const form = useForm({
    donationAmount: 100,
});

const paymentLink = ref('https://buy.stripe.com/test_6oEdUJ1PwdvN5gI003');

const imageOne = ref(false);
const imageTwo = ref(false);
const imageThree = ref(false);
const imageFour = ref(false);
const imageFive = ref(false);
const imageSix = ref(false);
const imageSeven = ref(false);
const imageEight = ref(false);
const imageNine = ref(false);
const imageTen = ref(false);

const toggleMobileMenu = () => {
    const mobileMenu = document.getElementById('mobileMenu');
    mobileMenu.classList.toggle('hidden');
}

const shouldShowBackground = () => {
    return imageOne.value
        || imageTwo.value
        || imageThree.value
        || imageFour.value
        || imageFive.value
        || imageSix.value
        || imageSeven.value
        || imageEight.value
        || imageNine.value
        || imageTen.value;
}

const handleClickOutside = (event) => {
    imageOne.value = false;
    imageTwo.value = false;
    imageThree.value = false;
    imageFour.value = false;
    imageFive.value = false;
    imageSix.value = false;
    imageSeven.value = false;
    imageEight.value = false;
    imageNine.value = false;
    imageTen.value = false;
}

watch(() => form.donationAmount, (value) => {
    if (value === 0) {
        location.href = paymentLink.value;
    }

    paymentLink.value = props.paymentLinks[value];
});
</script>
